class Quiz {
    constructor({}) {
        this.data = {};
    }
}

document.addEventListener('DOMContentLoaded', function() {

    const quizSlider = new Splide('.splide.quiz', {
        arrows: false,
        perPage: 1,
        perMove: 1,
        pagination: false,
        drag: false,
        gap: 120
    }).mount();

    document.querySelector('.quiz-actions__next').addEventListener('click', function(e) {
        quizSlider.go( '+' );
    });
    document.querySelector('.quiz-actions__prev').addEventListener('click', function(e) {
        quizSlider.go( '-' );
    });
});
